current.custom_css:
.bg-color-topbar-background.sf-topbar {
  border-bottom-color: #eeeeee;
}
div.slide__block-description {
  color: #666666;
}
.prod__trus-badges img {
  width: 357px;
}
.sf-footer .form-control.form__control-icon {
  background: #ffffff;
}
.sf__index-testimonial .sf-slider__controls-prev {
  left: -113px;
}
.sf__index-testimonial .sf-slider__controls-next {
  right: -113px;
}
.sf__index-testimonial .text-color-subtext + .not-change p {
  font-weight: 600;
}
.sf__index-testimonial .swiper-pagination-bullet {
  margin: 0 10px;
}
.sf__index-collection-list sup {
  top: -1em;
  right: -0.2em;
}

.sf__index-icon-box {
  border-top: 1px solid #eeeeee;
}
.sf__index-icon-box .section-py {
  padding-top: 40px;
  padding-bottom: 40px;
}
.sf__index-icon-box .sf-icon-box__text h3 {
  font-weight: 500;
  font-size: 20px;
}
.sf__index-icon-box .sf-icon-box__text .rte {
  color: #777777;
}
.sf__index-slideshow .sf__btn-link.-mt-1 {
  margin-top: 0;
}
.sf__index-collection-list .collection-card__info h3:hover {
  color: #666666;
}
.sf__index-testimonial .swiper-pagination {
  margin-top: 60px;
}
@media screen and (max-width: 1535px) {
  .sf__index-testimonial .swiper-pagination {
    margin-top: 50px;
  }
}

@media (min-width: 768px) {
  .sf__index-collection-list .section-my {
    margin-top: 30px;
  }
}

@media (max-width: 767px) {
  .sf__index-icon-box {
    border-top: 0px;
  }
  .sf__index-icon-box .section-py {
    padding-top: 20px;
  }
  .sf__index-blog-posts .sf__blog-post-button .sf__btn-secondary {
    width: 100%;
  }
}
@media (max-width: 576px) {
  .sf__index-slideshow .sf__btn-primary,
  .sf__index-featured-collection .sf__btn-secondary {
    width: 100%;
  }
}
.sf__choose-chair-box .section-py {
    padding-bottom: 0px;
  }

.sf__benefit-ergo-box .section-py {
  padding-top: 0px;
   padding-bottom: 0px;
  }
.sf__odinlake-box .section-py {
  padding-top: 30px;
   padding-bottom: 30px;
  }

.main-product__block+.main-product__block{
    margin-top: 15px;
margin-bottom: 15px;
}

.nav-swiper-container .swiper-slide-thumb-active .sf-prod-media{
  border: 1px solid rgb(192 192 192)
}
.sf-media-nav-mobile .swiper-slide-thumb-active .sf-prod-media{
  box-shadow: 0 0 0 1px #C0C0C0;
}

.text-color-regular-price {
font-weight:bold;
}

.sf__accordion-item .sf__accordion-button{
font-weight: 700;
}
.sf__odinlake-about-us .swiper-container-autoheight{
